mapper.select:
SELECT edi.*, u.user_name FROM express_delivery_info edi LEFT JOIN goods_order o ON edi.order_id = o.id LEFT JOIN user u ON o.user_id = u.id WHERE edi.date_delete IS NULL
, edi.order_id like concat('%',#{orderId},'%')
, edi.express_code like concat('%',#{expressCode},'%')
, edi.delivery_status = #{deliveryStatus}
, edi.express_carrier = #{expressCarrier}
ORDER BY edi.date_update
